import React, { Component } from "react";
import Axios from "axios";

import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBInput,
} from "mdbreact";

class LoginPage extends Component {
  state = {
    email: "",
    password: "",
  };

  submit = false;

  onLogin = (val) => {
    const { email, password } = this.state;
    console.log(email, password);
    Axios.post(
      "http://localhost/spare_parts/signin.php",
      JSON.stringify({
        email: email,
      })
    ).then((response) => {
      console.log(response.data[0].pwd, password);
      const status = response.data[0].pwd === password ? true : false;
      this.setState({ email: "", password: "" });
      if (status) this.props.history.push("/");
    });
  };

  render() {
    if (this.state.submit === true) {
      this.props.history.push("/");
    }
    const { email, password } = this.state;
    return (
      <MDBContainer>
        <MDBRow>
          <MDBCol md="6">
            <MDBCard>
              <div className="header pt-3 grey lighten-2">
                <MDBRow className="d-flex justify-content-start">
                  <h3 className="deep-grey-text mt-3 mb-4 pb-1 mx-5">Log in</h3>
                </MDBRow>
              </div>
              <MDBCardBody className="mx-4 mt-4">
                <MDBInput
                  label="Your email"
                  value={email}
                  group
                  type="text"
                  validate
                  onChange={(value) => this.setState({ email: value })}
                />
                <MDBInput
                  label="Your password"
                  value={password}
                  group
                  type="password"
                  validate
                  containerClass="mb-0"
                  onChange={(value) => this.setState({ password: value })}
                />
                <p className="font-small grey-text d-flex justify-content-end">
                  Forgot
                  <a href="#!" className="dark-grey-text font-weight-bold ml-1">
                    Password?
                  </a>
                </p>
                <div className="text-center mb-4 mt-5">
                  <MDBBtn
                    color="danger"
                    type="button"
                    className="btn-block z-depth-2"
                    onClick={() => this.onLogin(true)}
                  >
                    Log in
                  </MDBBtn>
                </div>
                <p className="font-small grey-text d-flex justify-content-center">
                  Don't have an account?
                  <a href="#!" className="dark-grey-text font-weight-bold ml-1">
                    Sign up
                  </a>
                </p>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
}

export default LoginPage;
