                                                <MDBCol sm="4" className="mb-3">
                                                    <MDBInput
                                                        label="Product Discount Price"
                                                        group
                                                        type="text"
                                                        validate
                                                        onChange={(event) =>
                                                            this.setState({
                                                                productDiscountPrice: event.target.value,
                                                            })
                                                        }
                                                        name="productDiscountPrice"
                                                    />
                                                </MDBCol>
                                                <MDBCol sm="4" className="mb-3">
                                                    <MDBInput
                                                        label="Product Status"
                                                        group
                                                        type="text"
                                                        validate
                                                        onChange={(event) =>
                                                            this.setState({
                                                                productStatus: event.target.value,
                                                            })
                                                        }
                                                        name="productStatus"
                                                    />
                                                </MDBCol>
                                            </MDBRow>
                                            <MDBRow>
                                                <input
                                                    type="file"
                                                    onChange={(event) =>
                                                        this.setState({
                                                            fileToUpload: event.target.files[0],
                                                        })
                                                    }
                                                ></input>
                                                {/* <ImageUploader
                          withIcon={true}
                          buttonText="Choose images"
