                          onChange={(picture) => console.log(picture)}
                          imgExtension={[".jpg", ".gif", ".png", ".gif"]}
                          maxFileSize={5242880}
                          withPreview={true}
                        /> */}
                                            </MDBRow>
                                        </div>
                                        <div className="text-center mt-4"></div>
                                    </form>
                                    <MDBBtn
                                        color="light-blue"
                                        className="mb-3"
                                        onClick={this.onAdd}
                                    >
                                        Add
                                    </MDBBtn>
                                </MDBCardBody>
                            </MDBCard>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        );
    }
}

