import React, { Component } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBIcon,
  MDBCardHeader,
  MDBBtn,
  MDBInput,
  MDBSelect,
  MDBTable,
  MDBTableBody,
  MDBTableHead,
} from "mdbreact";

class SalePage extends Component {
  state = {
    CustomerName: "",
    CategoryName: "",
  };

  getCurrentDate = (separator = "      ") => {
    let newDate = new Date();
    let date = newDate.getDate();
    let month = newDate.getMonth() + 1;
    let year = newDate.getFullYear();
    let hours = newDate.getHours();
    let mintues = newDate.getMinutes();
    let seconds = newDate.getSeconds();
    return `${year}/${
      month < 10 ? `0${month}` : `${month}`
    }/${date}${separator}${hours}:${mintues}:${seconds}`;
  };

  columns = [
    {
      label: "#",
      field: "id",
      sort: "asc",
    },
    {
      label: "Category",
      field: "category",
      sort: "asc",
      minimal: "lg",
    },
    {
      label: "Product",
      field: "product",
      sort: "asc",
      minimal: "lg",
    },
    {
      label: "Price",
      field: "price",
      sort: "asc",
      minimal: "lg",
    },
  ];

  rows_outline_btn = [
    {
      id: <h5 style={{ marginTop: 13, fontSize: 15, fontWeight: "300" }}>2</h5>,

      last: (
        <MDBSelect
          search
          style={{ margin: 0 }}
          options={this.state.categories}
          selected="Choose your option"
          getValue={(val) =>
            this.setState({
              categoryName: val[0],
            })
          }
        />
      ),
      price: (
        <h5 style={{ marginTop: 13, fontSize: 15, fontWeight: "500" }}>200</h5>
      ),
    },
  ];

  onAdd = () => {};

  categoryComponent = (
    <MDBSelect
      search
      style={{ margin: 0 }}
      options={this.state.categories}
      selected="Choose your option"
      getValue={(val) =>
        this.setState({
          categoryName: val[0],
        })
      }
    />
  );

  render() {
    return (
      <MDBContainer style={{ paddingTop: 50 }}>
        <MDBRow className="d-flex justify-content-center">
          <MDBCol size="12" xl="12">
            <MDBCard>
              <MDBCardBody>
                <MDBCardHeader className="form-header blue-gradient rounded">
                  <h3 className="my-3">
                    <MDBIcon icon="dollar-sign" /> Sale
                  </h3>
                </MDBCardHeader>
                <form>
                  <div className="grey-text">
                    {/*---------- Row 1 ----------*/}
                    <MDBRow>
                      <MDBCol sm="4" className="mb-3">
                        <MDBInput
                          label="Customer Name"
                          group
                          type="text"
                          validate
                          onChange={(event) =>
                            this.setState({ CustomerName: event.target.value })
                          }
                          name="productName"
                        />
                      </MDBCol>
                      <MDBCol sm="1" className="mb-3"></MDBCol>
                      <MDBCol sm="4" className="mb-3">
                        <MDBInput
                          label="Date"
                          disabled={true}
                          group
                          type="text"
                          value={this.getCurrentDate()}
                        />
                      </MDBCol>
                      <MDBCol sm="3">
                        <MDBBtn color="red" onClick={null}>
                          Add
                        </MDBBtn>
                      </MDBCol>
                    </MDBRow>
                    <MDBRow>
                      <MDBCol>
                        <MDBTable>
                          <caption>List of users</caption>
                          <MDBTableHead>
                            <tr>
                              <th>#</th>
                              <th>Category</th>
                              <th>Product</th>
                              <th>Price</th>
                            </tr>
                          </MDBTableHead>
                          <MDBTableBody>
                            <tr>
                              <td>1</td>
                              <td>Mark</td>
                              <td>Otto</td>
                              <td>@mdo</td>
                            </tr>
                            <tr>
                              <td>2</td>
                              <td>Jacob</td>
                              <td>Thornton</td>
                              <td>@fat</td>
                            </tr>
                            <tr>
                              <td>3</td>
                              <td>Larry</td>
                              <td>the Bird</td>
                              <td>@twitter</td>
                            </tr>
                          </MDBTableBody>
                        </MDBTable>
                      </MDBCol>
                    </MDBRow>
                  </div>
                  <div className="text-center mt-4"></div>
                </form>
                <MDBBtn
                  color="light-blue"
                  className="mb-3"
                  onClick={this.onAdd}
                >
                  Add
                </MDBBtn>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
}

export default SalePage;
