import React, {Component} from "react";
import {MDBDataTable} from 'mdbreact';
import "./all_product.css"
import axios from "axios";

class AllProducts extends Component {

    componentDidMount = () => {
        let {categories, products} = this.state;
        axios
            .post("http://localhost/spare_parts/allproducts.php")
            .then((response) => {
                    response.data.map((product) => {

                        const {Product_Name, Category_Name, Product_Retail_Price,id, Product_Quantity } = product;
                        const newProduct = {}
                        newProduct[Product_Name] = {
                            name: Product_Name,
                            value: Product_Name,
                            price: parseInt(Product_Retail_Price),
                            id:id,
                            qty: Product_Quantity,

                        };
                        if (!products.hasOwnProperty(product.Category_Name))
                            products[Category_Name] = newProduct;
                        else
                            products[Category_Name][Product_Name] = newProduct[Product_Name];
                    });
                }
            );
        this.setState({products, categories});
    };

    quantityComponent = (index) =>{
        return (
            <div className="md-form" style={{margin: 0}}>
                <input
                    type="number"
                    id={index + 1}
                    onChange={event => this.onQuantityChange(index, event.target.value)}
                    min={0}
                    className="form-control"
                />
            </div>
        );
    }

    onQuantityChange = (index, value) => {
        if (value !== ""){
            let { data } = this.state;
            data.rows[index].qty = parseInt(value);
            this.setState({data})


        }

    }

    state = {
        data: {
            columns: [
                {
                    label: 'Name',
                    field: 'name',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'Position',
                    field: 'position',
                    sort: 'asc',
                    width: 270
                },
                {
                    label: 'Office',
                    field: 'office',
                    sort: 'asc',
                    width: 200
                },
                {
                    label: 'Age',
                    field: 'age',
                    sort: 'asc',
                    width: 100
                },
                {
                    label: 'Start date',
                    field: 'date',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'Salary',
                    field: 'salary',
                    sort: 'asc',
                    width: 100
                }
            ],
            rows: [
                {
                    name: 'Tiger Nixon',
                    position: 'System Architect',
                    office: 'Edinburgh',
                    age: '61',
                    date: '2011/04/25',
                    qty: 10,
                    salary: '$320'
                },
                {
                    name: 'Garrett Winters',
                    position: 'Accountant',
                    office: 'Tokyo',
                    age: '63',
                    date: this.quantityComponent(1),
                    qty: 10,
                    salary: '$170'
                },
                {
                    name: 'Ashton Cox',
                    position: 'Junior Technical Author',
                    office: 'San Francisco',
                    age: '66',
                    date: '2009/01/12',
                    qty: 10,
                    salary: '$86'
                },
                {
                    name: 'Cedric Kelly',
                    position: 'Senior Javascript Developer',
                    office: 'Edinburgh',
                    age: '22',
                    date: '2012/03/29',
                    qty: 10,
                    salary: '$433'
                },
                {
                    name: 'Airi Satou',
                    position: 'Accountant',
                    office: 'Tokyo',
                    age: '33',
                    date: '2008/11/28',
                    qty: 10,
                    salary: '$162'
                },
            ]
        }
    }

    render() {
        return (
            <div>
                <MDBDataTable
                    // className="form-header blue-gradient rounded"
                    theadColor={"blue-gradient"}
                    striped
                    barReverse={true}
                    theadTextWhite={true}
                    hover
                    noBottomColumns={true}
                    data={this.state.data}
                />
                <button onClick={() => console.log(this.state.data)}>
                    press
                </button>
            </div>
        );
    }
}

export default AllProducts;
