import React,{Component} from "react";
import {MDBBtn, MDBCol, MDBContainer, MDBDataTable, MDBRow} from "mdbreact";

class AllExpanses extends Component{
    state = {
        data: {
            columns: [
                {
                    label: 'Name',
                    field: 'Product_Name',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'C.Name',
                    field: 'Category_Name',
                    sort: 'asc',
                    width: 270
                },
                {
                    label: 'P.Brand',
                    field: 'Product_Brand',
                    sort: 'asc',
                    width: 200
                },
                {
                    label: 'P.Code',
                    field: 'Product_Code',
                    sort: 'asc',
                    width: 100
                },
                {
                    label: 'Qty',
                    field: 'qty',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'R.Price',
                    field: 'Product_Retail_Price',
                    sort: 'asc',
                    width: 350
                },
                {
                    label: 'Profit',
                    field: 'Profit',
                    sort: 'asc',
                    width: 350
                },
            ],
            rows: []
        },
        grossProfit: 0
    };

    render() {
        const { grossProfit } = this.state;
        let { columns, rows } = this.state.data;
        if (rows.length > 0)
            rows.map((value, index) => {
                rows[index].qty = this.quantityComponent(index);
                // rows[index].Product_PurchasePrice;
                // rows[index].Product_Retail_Price;
            })
        return (
            <div>
                <MDBDataTable
                    // className="form-header blue-gradient rounded"
                    theadColor={"blue-gradient"}
                    striped
                    barReverse={true}
                    theadTextWhite={true}
                    hover
                    noBottomColumns={true}
                    data={{columns,rows}}
                />
                <MDBContainer style={{paddingTop: 50}}>
                    <MDBRow className="d-flex justify-content-center">
                        <MDBCol sm={"8"}>
                            <MDBBtn color={"success"} onClick={this.onSave}>Save</MDBBtn>
                        </MDBCol>
                        <MDBCol sm={4}>
                            <span style={{fontSize:"20px", fontWeight:"bold"}}>Gross Profit: </span>
                            <div style={{display:"inline-block"}}>
                                <h4 style={{color:"#00C851"}}>{ `Rs.${grossProfit}` }</h4>
                            </div>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        );
    }
}

export default AllExpanses;
