import React, {Component} from "react";
import {MDBDataTable} from 'mdbreact';
import "./all_product.css"
import axios from "axios";

class AllProducts extends Component {

    componentDidMount = async () => {
        let {data} = this.state;
        let rows = data.rows;
        await axios
            .post("http://localhost/spare_parts/allproducts.php")
            .then((response) => {
                    response.data.map((product, index) => {
                        const {
                            id,
                            Product_Name,
                            Category_Name,
                            Product_Brand,
                            Product_Model,
                            Product_Code,
                            Product_Quantity,
                            Product_Size,
                            Product_Color,
                            Product_PurchasePrice,
                            Product_Retail_Price,
                            Product_Status,
                            Product_Discounted_Price,
                            Product_Purchase_Date,
                        } = product;
                        let newProduct = {};
                        newProduct = {
                            id,
                            Product_Name: Product_Name,
                            Category_Name,
                            Product_Brand,
                            Product_Model,
                            Product_Code,
                            qty: this.quantityComponent(index),
                            Product_Quantity,
                            Product_Size,
                            Product_Color,
                            Product_PurchasePrice,
                            Product_Retail_Price,
                            Product_Status,
                            Product_Discounted_Price,
                            Product_Purchase_Date,
                        };
                        rows = [...rows, newProduct];
                    });
                }
            );
        data.rows = rows;
        console.log(data);
        this.setState({data});
    };

    quantityComponent = (index) =>{
        return (
            <div className="md-form" style={{margin: 0}}>
                <input
                    type="number"
                    id={index + 1}
                    onChange={event => this.onQuantityChange(index, event.target.value)}
                    min={0}
                    className="form-control"
                />
            </div>
        );
    }

    onQuantityChange = (index, value) => {
        if (value !== ""){
            let { data } = this.state;
            data.rows[index].Product_Quantity = parseInt(value);
            this.setState({data})
        }

    }

    state = {
        data: {
            columns: [
                {
                    label: 'Name',
                    field: 'Product_Name',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'C.Name',
                    field: 'Category_Name',
                    sort: 'asc',
                    width: 270
                },
                {
                    label: 'P.Brand',
                    field: 'Product_Brand',
                    sort: 'asc',
                    width: 200
                },
                {
                    label: 'P.Code',
                    field: 'Product_Code',
                    sort: 'asc',
                    width: 100
                },
                {
                    label: 'R.Price',
                    field: 'Product_Retail_Price',
                    sort: 'asc',
                    width: 150
                },
            ],
            rows: [
                {
                    Product_Name: 'Cedric Kelly',
                    position: 'Senior Javascript Developer',
                    office: 'Edinburgh',
                    age: '22',
                    date: '2012/03/29',
                    qty: 10,
                    salary: '$433'
                }
            ]
        }
    }

    render() {
        return (
            <div>
                <MDBDataTable
                    // className="form-header blue-gradient rounded"
                    theadColor={"blue-gradient"}
                    striped
                    barReverse={true}
                    theadTextWhite={true}
                    hover
                    noBottomColumns={true}
                    data={this.state.data}
                />
                <button onClick={() => console.log(this.state.data)}>
                    press
                </button>
            </div>
        );
    }
}

export default AllProducts;





