import React, {Component} from "react";

import {Document, Image, Page, StyleSheet, PDFViewer} from '@react-pdf/renderer';
import InvoiceTitle from 'invoiceTitle'
import BillTo from './BillTo'
import InvoiceNo from './InvoiceNo'
import InvoiceItemsTable from './InvoiceItemTable'
import InvoiceThankYouMsg from './InvoiceThankYouMsg'
import logo from '../../../logo.svg'


class Invoice extends Component {
    state = {}

    invoiceData = {
        "id": "5f8920556ab6bcf247b2115c",
        "invoice_no": "202007-31",
        "balance": "$1,941.94",
        "company": "VOIPA",
        "email": "haleyblake@voipa.com",
        "phone": "+1 (830) 481-3846",
        "address": "437 Manhattan Court, Babb, Mississippi, 7475",
        "trans_date": "2019-09-01",
        "due_date": "2019-10-22",
        "items": [
            {
                "sno": 1,
                "desc": "nisi sunt consectetur labore magna",
                "qty": 6,
                "rate": 360.61
            },
            {
                "sno": 2,
                "desc": "sit duis in do non",
                "qty": 3,
                "rate": 879.56
            },
            {
                "sno": 3,
                "desc": "amet reprehenderit id nisi aute",
                "qty": 3,
                "rate": 1094.78
            },
            {
                "sno": 4,
                "desc": "pariatur non sit voluptate elit",
                "qty": 10,
                "rate": 1021.49
            },
            {
                "sno": 5,
                "desc": "tempor enim aliqua duis esse",
                "qty": 5,
                "rate": 615.32
            }
        ]
    }

    styles = StyleSheet.create({
        page: {
            fontFamily: 'Helvetica',
            fontSize: 11,
            paddingTop: 30,
            paddingLeft: 60,
            paddingRight: 60,
            lineHeight: 1.5,
            flexDirection: 'column',
        },
        logo: {
            width: 74,
            height: 66,
            marginLeft: 'auto',
            marginRight: 'auto'
        }
    });

    render() {
        const invoice  = this.invoiceData;
        return (
            <PDFViewer width="1000" height="600" className="app" >
                <Document>
                    <Page size="A4" style={this.styles.page}>
                        <Image style={this.styles.logo} src={logo}/>
                        <InvoiceTitle title='Invoice'/>
                        <InvoiceNo invoice={invoice}/>
                        <BillTo invoice={invoice}/>
                        <InvoiceItemsTable invoice={invoice}/>
                        <InvoiceThankYouMsg/>
                    </Page>
                </Document>
            </PDFViewer>
        );
    }
}

export default Invoice;
