import React, {Component} from "react";
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBCardHeader,
    MDBCol,
    MDBContainer,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBSelect,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
} from "mdbreact";
import axios from "axios";

import getCurrentDate from "../utils/get_current_date"

class SalePage extends Component {
    notSelected = "Chose your option";
    state = {
        CustomerName: "",
        SelectedCategories: [
            this.notSelected,
        ],
        SelectedProducts: [
            this.notSelected,
        ],
        categories: [],
        products: {
            wash: [
                {
                    text: "wash1",
                    value: "wash1"
                },
                {
                    text: "wash2",
                    value: "wash2"
                },
                {
                    text: "wash3",
                    value: "wash3"
                },
            ],
            oven: [
                {
                    text: "oven1",
                    value: "oven1"
                },
                {
                    text: "oven2",
                    value: "wash2"
                },
                {
                    text: "oven3",
                    value: "oven3"
                },
            ],
            laptop: [
                {
                    text: "laptop1",
                    value: "laptop1"
                },
                {
                    text: "laptop2",
                    value: "laptop2"
                },
                {
                    text: "laptop3",
                    value: "laptop3"
                },
            ],

        },
    };

    componentDidMount =  () =>  {
        let {categories} = this.state;
        axios
            .post("http://localhost/spare_parts/show_category.php")
            .then((response) => {
                    response.data.map((category) => {
                        const newCategory = {text: category.Category_Name, value: category.Category_Name};
                        categories = [...categories, newCategory];
                    });
                    this.setState({categories})
                }
            );

    };

    onAdd = () => {
    };

    categoryComponent = (id) => {
        const index = id - 1;
        return (
            <MDBSelect
                search
                style={{margin: 0}}
                options={this.state.categories}
                selected={this.state.SelectedCategories[index]}
                getValue={(selectedValue) => this.onSelectedCategoryUpdate(id, selectedValue[0])}
            />
        );
    };

    onSelectedCategoryUpdate = (id, selectedValue) => {
        const index = id - 1;
        let {SelectedCategories} = this.state;
        SelectedCategories[index] = selectedValue;
        this.setState({SelectedCategories});
    };

    productComponent = (id) => {
        const {products, SelectedCategories, SelectedProducts} = this.state;
        const index = id - 1;
        return (
            <MDBSelect
                search
                style={{margin: 0}}
                options={products[SelectedCategories[index]]}
                selected={SelectedProducts[index]}
                getValue={(selectedValue) => this.onSelectedProductUpdate(id, selectedValue[0])}
            />
        );
    };

    onSelectedProductUpdate = (id, selectedValue) => {
        const index = id - 1;
        let {SelectedProducts} = this.state;
        SelectedProducts[index] = selectedValue;
        this.setState({SelectedProducts});
    };

    render() {
        return (
            <MDBContainer style={{paddingTop: 50}}>
                <MDBRow className="d-flex justify-content-center">
                    <MDBCol size="12" xl="12">
                        <MDBCard>
                            <MDBCardBody>
                                <MDBCardHeader className="form-header blue-gradient rounded">
                                    <h3 className="my-3">
                                        <MDBIcon icon="dollar-sign"/> Sale
                                    </h3>
                                </MDBCardHeader>
                                <form>
                                    <div className="grey-text">
                                        {/*---------- Row 1 ----------*/}
                                        <MDBRow>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Customer Name"
                                                    group
                                                    type="text"
                                                    validate
                                                    onChange={(event) =>
                                                        this.setState({CustomerName: event.target.value})
                                                    }
                                                    name="productName"
                                                />
                                            </MDBCol>
                                            <MDBCol sm="1" className="mb-3"/>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Date"
                                                    disabled={true}
                                                    group
                                                    type="text"
                                                    value={getCurrentDate()}
                                                />
                                            </MDBCol>
                                            <MDBCol sm="3">
                                                <MDBBtn color="red" onClick={null}>
                                                    Add
                                                </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol>
                                                <MDBTable>
                                                    <caption>List of users</caption>
                                                    <MDBTableHead>
                                                        <tr>
                                                            <th>#</th>
                                                            <th>Category</th>
                                                            <th>Product</th>
                                                            <th>Price</th>
                                                        </tr>
                                                    </MDBTableHead>
                                                    <MDBTableBody>
                                                        <tr>
                                                            <td>1</td>
                                                            <td>{this.categoryComponent(1)}</td>
                                                            <td>{this.productComponent(1)}</td>
                                                            <td>2000</td>
                                                        </tr>
                                                        <tr>
                                                            <td>2</td>
                                                            <td>{this.categoryComponent(2)}</td>
                                                            <td>{this.productComponent(2)}</td>
                                                            <td>2000</td>
                                                        </tr>
                                                    </MDBTableBody>
                                                </MDBTable>
                                            </MDBCol>
                                        </MDBRow>
                                    </div>
                                    <div className="text-center mt-4"/>
                                </form>
                                <MDBBtn
                                    color="light-blue"
                                    className="mb-3"
                                    onClick={this.onAdd}
                                >
                                    Add
                                </MDBBtn>
                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        );
    }
}

export default SalePage;
