import React, { Component } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBIcon,
  MDBCardHeader,
  MDBBtn,
  MDBInput,
  MDBSelect,
} from "mdbreact";

class SalePage extends Component {
  state = {
    CustomerName: "",
    CategoryName: "",
  };
  render() {
    return (
      <MDBContainer style={{ paddingTop: 50 }}>
        <MDBRow className="d-flex justify-content-center">
          <MDBCol size="12" xl="12">
            <MDBCard>
              <MDBCardBody>
                <MDBCardHeader className="form-header blue-gradient rounded">
                  <h3 className="my-3">
                    <MDBIcon icon="dollar-sign" /> Sale
                  </h3>
                </MDBCardHeader>
                <form>
                  <div className="grey-text">
                    {/*---------- Row 1 ----------*/}
                    <MDBRow>
                      <MDBCol sm="4" className="mb-3">
                        <MDBInput
                          label="Customer Name"
                          group
                          type="text"
                          validate
                          onChange={(event) =>
                            this.setState({ CustomerName: event.target.value })
                          }
                          name="productName"
                        />
                      </MDBCol>
                      <MDBCol sm="1" className="mb-3"></MDBCol>
                      <MDBCol sm="4" className="mb-3">
                        <MDBInput
                          label="Product Brand"
                          disabled={true}
                          group
                          type="text"
                          validate
                          onChange={(event) =>
                            this.setState({
                              productBrand: event.target.value,
                            })
                          }
                          name="productBrand"
                        />
                      </MDBCol>
                    </MDBRow>
                    <MDBRow>
                      <input
                        type="file"
                        onChange={(event) =>
                          this.setState({
                            fileToUpload: event.target.files[0].name,
                          })
                        }
                      ></input>
                      {/* <ImageUploader
                          withIcon={true}
                          buttonText="Choose images"
                          singleImage={true}
                          onChange={(picture) => console.log(picture)}
                          imgExtension={[".jpg", ".gif", ".png", ".gif"]}
                          maxFileSize={5242880}
                          withPreview={true}
                        /> */}
                    </MDBRow>
                  </div>
                  <div className="text-center mt-4"></div>
                </form>
                <MDBBtn
                  color="light-blue"
                  className="mb-3"
                  onClick={this.onAdd}
                >
                  Add
                </MDBBtn>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
}

export default SalePage;
