import React, {Component} from "react";
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBCardHeader,
    MDBCol,
    MDBContainer,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBSelect,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
} from "mdbreact";
import axios from "axios";

import getCurrentDate from "../utils/get_current_date"

class SalePage extends Component {
    notSelected = "Chose your option";
    state = {
        CustomerName: "",
        rowCount: 1,
        SelectedCategories: [this.notSelected],
        SelectedProducts: [this.notSelected],
        SelectedProductPrices: [0],
        categories: [
            // {
            //     text: "Category Name",
            //     value: "Category Value"
            // },
        ],
        products: {
            // Data Format
            // Category_Name: [
            //     {
            //         text: "Product Name",
            //         value: "Product Value"
            //         price: "Product Price"
            //     },
            // ],
        },
    };

    componentDidMount =  () =>  {
        let {categories, products} = this.state;
        axios
            .post("http://localhost/spare_parts/show_category.php")
            .then((response) => {
                    response.data.map((category) => {
                        const newCategory = {text: category.Category_Name, value: category.Category_Name};
                        categories = [...categories, newCategory];
                    });
                    this.setState({categories})
                }
            );
        axios
            .post("http://localhost/spare_parts/allproducts.php")
            .then((response) => {
                    response.data.map((product) => {

                        const newProduct = {text: product.Product_Name, value: product.Product_Name};
                        if (!products.hasOwnProperty(product.Category_Name)) products[product.Category_Name] = [newProduct];
                        else products[product.Category_Name] = [...products[product.Category_Name], newProduct];
                    });
                    this.setState({ products })
                }
            );

    };

    onAdd = () => {
    };

    categoryComponent = (id) => {
        const index = id - 1;
        return (
            <MDBSelect
                search
                style={{margin: 0}}
                options={this.state.categories}
                selected={this.state.SelectedCategories[index]}
                getValue={(selectedValue) => this.onSelectedCategoryUpdate(id, selectedValue[0])}
            />
        );
    };

    onSelectedCategoryUpdate = (id, selectedValue) => {
        const index = id - 1;
        let {SelectedCategories} = this.state;
        SelectedCategories[index] = selectedValue;
        this.setState({SelectedCategories});
    };

    productComponent = (id) => {
        const {products, SelectedCategories, SelectedProducts} = this.state;
        const index = id - 1;
        return (
            <MDBSelect
                search
                style={{margin: 0}}
                options={products[SelectedCategories[index]]}
                selected={SelectedProducts[index]}
                getValue={(selectedValue) => this.onSelectedProductUpdate(id, selectedValue[0])}
            />
        );
    };

    onSelectedProductUpdate = (id, selectedValue) => {
        const index = id - 1;
        let {SelectedProducts} = this.state;
        SelectedProducts[index] = selectedValue;
        this.setState({SelectedProducts});
    };

    render() {
        return (
            <MDBContainer style={{paddingTop: 50}}>
                <MDBRow className="d-flex justify-content-center">
                    <MDBCol size="12" xl="12">
                        <MDBCard>
                            <MDBCardBody>
                                <MDBCardHeader className="form-header blue-gradient rounded">
                                    <h3 className="my-3">
                                        <MDBIcon icon="dollar-sign"/> Sale
                                    </h3>
                                </MDBCardHeader>
                                <form>
                                    <div className="grey-text">
                                        {/*---------- Row 1 ----------*/}
                                        <MDBRow>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Customer Name"
                                                    group
                                                    type="text"
                                                    validate
                                                    onChange={(event) =>
                                                        this.setState({CustomerName: event.target.value})
                                                    }
                                                    name="productName"
                                                />
                                            </MDBCol>
                                            <MDBCol sm="1" className="mb-3"/>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Date"
                                                    disabled={true}
                                                    group
                                                    type="text"
                                                    value={getCurrentDate()}
                                                />
                                            </MDBCol>
                                            <MDBCol sm="3">
                                                <MDBBtn color="red" onClick={null}>
                                                    Add
                                                </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol>
                                                <MDBTable>
                                                    <caption>List of users</caption>
                                                    <MDBTableHead>
                                                        <tr>
                                                            <th>#</th>
                                                            <th>Category</th>
                                                            <th>Product</th>
                                                            <th>Price</th>
                                                        </tr>
                                                    </MDBTableHead>
                                                    <MDBTableBody>

                                                        <tr>
                                                            <td>1</td>
                                                            <td>{this.categoryComponent(1)}</td>
                                                            <td>{this.productComponent(1)}</td>
                                                            <td>{this.state.SelectedProductPrices[0]}</td>
                                                        </tr>
                                                        <tr>
                                                            <td>2</td>
                                                            <td>{this.categoryComponent(2)}</td>
                                                            <td>{this.productComponent(2)}</td>
                                                            <td>2000</td>
                                                        </tr>
                                                    </MDBTableBody>
                                                </MDBTable>
                                            </MDBCol>
                                        </MDBRow>
                                    </div>
                                    <div className="text-center mt-4"/>
                                </form>
                                <MDBBtn
                                    color="light-blue"
                                    className="mb-3"
                                    onClick={this.onAdd}
                                >
                                    Add
                                </MDBBtn>
                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        );
    }
}

export default SalePage;
