import React, {Component} from "react";
import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBCard,
    MDBCardBody,
    MDBIcon,
    MDBCardHeader,
    MDBBtn,
    MDBInput,
    MDBSelect,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
} from "mdbreact";

class SalePage extends Component {
    state = {
        CustomerName: "",
        CategoryName: "",
        options: [
            {
                text: "wash",
                value: "wash",
            },
            {
                text: "laptop",
                value: "laptop"
            },
            {
                text: "oven",
                value: "oven"
            },
        ],
        products: {
            wash: [
                {
                    text: "wash1",
                    value: "wash1"
                },
                {
                    text: "wash2",
                    value: "wash2"
                },
                {
                    text: "wash3",
                    value: "wash3"
                },
            ],
            oven: [
                {
                    text: "oven1",
                    value: "oven1"
                },
                {
                    text: "oven2",
                    value: "wash2"
                },
                {
                    text: "oven3",
                    value: "oven3"
                },
            ],
            laptop: [
                {
                    text: "laptop1",
                    value: "laptop1"
                },
                {
                    text: "laptop2",
                    value: "laptop2"
                },
                {
                    text: "laptop3",
                    value: "laptop3"
                },
            ],

        },
    };

    getCurrentDate = (separator = "      ") => {
        let newDate = new Date();
        let date = newDate.getDate();
        let month = newDate.getMonth() + 1;
        let year = newDate.getFullYear();
        let hours = newDate.getHours();
        let mintues = newDate.getMinutes();
        let seconds = newDate.getSeconds();
        return `${year}/${
            month < 10 ? `0${month}` : `${month}`
        }/${date}${separator}${hours}:${mintues}:${seconds}`;
    };

    onAdd = () => {
    };

    categoryComponent = (
        <MDBSelect
            search
            style={{margin: 0}}
            options={this.state.options}
            selected="Choose your option"
            getValue={(val) =>
                this.setState({
                    categoryName: val[0],
                })
            }
        />
    );

    productComponent = (
        <MDBSelect
            search
            style={{margin: 0}}
            options={null}
            selected="Choose your option"
            getValue={(val) =>
                this.setState({
                    categoryName: val[0],
                })
            }
        />
    );

    render() {
        return (
            <MDBContainer style={{paddingTop: 50}}>
                <MDBRow className="d-flex justify-content-center">
                    <MDBCol size="12" xl="12">
                        <MDBCard>
                            <MDBCardBody>
                                <MDBCardHeader className="form-header blue-gradient rounded">
                                    <h3 className="my-3">
                                        <MDBIcon icon="dollar-sign"/> Sale
                                    </h3>
                                </MDBCardHeader>
                                <form>
                                    <div className="grey-text">
                                        {/*---------- Row 1 ----------*/}
                                        <MDBRow>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Customer Name"
                                                    group
                                                    type="text"
                                                    validate
                                                    onChange={(event) =>
                                                        this.setState({CustomerName: event.target.value})
                                                    }
                                                    name="productName"
                                                />
                                            </MDBCol>
                                            <MDBCol sm="1" className="mb-3"/>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Date"
                                                    disabled={true}
                                                    group
                                                    type="text"
                                                    value={this.getCurrentDate()}
                                                />
                                            </MDBCol>
                                            <MDBCol sm="3">
                                                <MDBBtn color="red" onClick={null}>
                                                    Add
                                                </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol>
                                                <MDBTable>
                                                    <caption>List of users</caption>
                                                    <MDBTableHead>
                                                        <tr>
                                                            <th>#</th>
                                                            <th>Category</th>
                                                            <th>Product</th>
                                                            <th>Price</th>
                                                        </tr>
                                                    </MDBTableHead>
                                                    <MDBTableBody>
                                                        <tr>
                                                            <td>1</td>
                                                            <td><MDBSelect
                                                                search
                                                                style={{margin: 0}}
                                                                options={this.state.options}
                                                                selected="Choose your option"
                                                                getValue={(val) =>
                                                                    this.setState({
                                                                        categoryName: val[0],
                                                                    })
                                                                }
                                                            /></td>
                                                            <td><MDBSelect
                                                                search
                                                                style={{margin: 0}}
                                                                options={null}
                                                                selected="Choose your option"
                                                                getValue={(val) =>
                                                                    this.setState({
                                                                        categoryName: val[0],
                                                                    })
                                                                }
                                                            /></td>
                                                            <td>@mdo</td>
                                                        </tr>
                                                    </MDBTableBody>
                                                </MDBTable>
                                            </MDBCol>
                                        </MDBRow>
                                    </div>
                                    <div className="text-center mt-4"></div>
                                </form>
                                <MDBBtn
                                    color="light-blue"
                                    className="mb-3"
                                    onClick={this.onAdd}
                                >
                                    Add
                                </MDBBtn>
                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        );
    }
}

export default SalePage;
