import React, {Component} from "react";
import {MDBDataTable, MDBInput} from 'mdbreact';
import "./all_product.css"

class AllProducts extends Component {
    quantityComponent = (index) =>{
        return (
            <div className="md-form" style={{margin: 0}}>
                <input
                    type="number"
                    id={index + 1}
                    onChange={event => this.onQuantityChange(index, event.target.value)}
                    min={0}
                    className="form-control"
                />
            </div>
        );
    }

    onQuantityChange = (index, value) => {
        if (value !== ""){
            let { data } = this.state;
            const quantity = parseInt(value);

        }

    }

    state = {
        data: {
            columns: [
                {
                    label: 'Name',
                    field: 'name',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'Position',
                    field: 'position',
                    sort: 'asc',
                    width: 270
                },
                {
                    label: 'Office',
                    field: 'office',
                    sort: 'asc',
                    width: 200
                },
                {
                    label: 'Age',
                    field: 'age',
                    sort: 'asc',
                    width: 100
                },
                {
                    label: 'Start date',
                    field: 'date',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'Salary',
                    field: 'salary',
                    sort: 'asc',
                    width: 100
                }
            ],
            rows: [
                {
                    name: 'Tiger Nixon',
                    position: 'System Architect',
                    office: 'Edinburgh',
                    age: '61',
                    date: '2011/04/25',
                    qty: 10,
                    salary: '$320'
                },
                {
                    name: 'Garrett Winters',
                    position: 'Accountant',
                    office: 'Tokyo',
                    age: '63',
                    date: this.quantityComponent(5),
                    qty: 10,
                    salary: '$170'
                },
                {
                    name: 'Ashton Cox',
                    position: 'Junior Technical Author',
                    office: 'San Francisco',
                    age: '66',
                    date: '2009/01/12',
                    qty: 10,
                    salary: '$86'
                },
                {
                    name: 'Cedric Kelly',
                    position: 'Senior Javascript Developer',
                    office: 'Edinburgh',
                    age: '22',
                    date: '2012/03/29',
                    qty: 10,
                    salary: '$433'
                },
                {
                    name: 'Airi Satou',
                    position: 'Accountant',
                    office: 'Tokyo',
                    age: '33',
                    date: '2008/11/28',
                    qty: 10,
                    salary: '$162'
                },
            ]
        }
    }

    render() {
        return (
            <MDBDataTable
                // className="form-header blue-gradient rounded"
                theadColor={"blue-gradient"}
                striped
                barReverse={true}
                theadTextWhite={true}
                hover
                noBottomColumns={true}
                data={this.state.data}
            />
        );
    }
}

export default AllProducts;
