import React, {Component} from "react";
import lodash from "lodash"
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBCardHeader,
    MDBCol,
    MDBContainer,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBSelect,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBLink,
    MDBDatePicker
} from "mdbreact";
import axios from "axios";

import getCurrentDate from "../utils/get_current_date"

const Row = ({id, categoryComponent, productComponent, price}) => {
    return (
        <tr>
            <td>{id}</td>
            <td>{categoryComponent(id)}</td>
            <td>{productComponent(id)}</td>
            <td>{price}</td>
        </tr>
    );
}


class SalePage extends Component {
    notSelected = "Chose your option";

    onEnter = () =>{
        const {
            SelectedProductPrices,
            SelectedProducts,
            rowCount,
            subTotal,
            discount,
            total,
            customerName,
        } = this.state;
        //    Data Format
        // {
        //  sno: index
        //  dec: Product Name,
        //  qty: 1 (Quantity)
        //  rate: Price
        // }
         let items = lodash.range(rowCount).map(index => {
             return {
                 sno: index,
                 desc: SelectedProducts[index],
                 qty: 1,
                 rate: SelectedProductPrices[index]
             }
         })
        return {
            subTotal: subTotal,
            discount: discount,
            total: total,
            items: items,
            customerName: customerName
        }
    }


    state = {
        isMounted: false,
        customerName: "",
        rowCount: 1,
        Items: this.onEnter,
        SelectedCategories: [this.notSelected],
        SelectedProducts: [this.notSelected],
        SelectedProductPrices: [0],
        subTotal: 0,
        discount: 0,
        total: 0,
        categories: [
            // {
            //     text: "Category Name",
            //     value: "Category Value"
            // },
        ],
        products: {
            // Data Format
            // Category_Name: {
            //     Product_Name: {
            //         text: "Product Name",
            //         value: "Product Value"
            //         price: "Product Price"
            //     },
            // },
        },
    };

    componentDidMount = async () => {
        let {categories, products} = this.state;
        await axios
            .post("http://localhost/spare_parts/show_category.php")
            .then((response) => {
                    response.data.map((category) => {
                        const newCategory = {text: category.Category_Name, value: category.Category_Name};
                        categories = [...categories, newCategory];
                    });
                }
            );
        await axios
            .post("http://localhost/spare_parts/allproducts.php")
            .then((response) => {
                    response.data.map((product) => {

                        const {Product_Name, Category_Name, Product_Retail_Price} = product;
                        const newProduct = {}
                        newProduct[Product_Name] = {
                            text: Product_Name,
                            value: Product_Name,
                            price: parseInt(Product_Retail_Price)
                        };
                        if (!products.hasOwnProperty(product.Category_Name))
                            products[Category_Name] = newProduct;
                        else
                            products[Category_Name][Product_Name] = newProduct[Product_Name];
                    });
                }
            );
        this.setState({products, categories})
        this.setState({isMounted: true})
    };

    onAdd = () => {
        let {rowCount,
            SelectedProducts,
            SelectedCategories,
            SelectedProductPrices
        } = this.state;
        SelectedProductPrices[rowCount] = 0;
        SelectedProducts[rowCount] = this.notSelected;
        SelectedCategories[rowCount] = this.notSelected;
        rowCount += 1;
        this.setState({SelectedCategories, SelectedProducts, SelectedProductPrices, rowCount});
    };

    categoryComponent = (id) => {
        const index = id - 1;
        return (
            <MDBSelect
                search
                style={{margin: 0}}
                getValue={selectedValue => this.onSelectedCategoryUpdate(id, selectedValue[0])}
                options={this.state.categories}
                selected={this.state.SelectedCategories[index]}
            />
        );
    };

    onSelectedCategoryUpdate = (id, selectedValue) => {
        let {
            isMounted,
            SelectedCategories,
            discount
        } = this.state;


        if (isMounted
            && typeof selectedValue != "undefined"
            && selectedValue !== SelectedCategories[id - 1]
        ) {
            const index = id - 1;
            SelectedCategories[index] = selectedValue;
            const subTotal = this.computeSubtotal();
            const total = subTotal - discount;
            this.setState({SelectedCategories, subTotal, total});
        }
    };

    productComponent = (id) => {
        const {products, SelectedCategories, SelectedProducts} = this.state;
        const index = id - 1;
        let options;
        if (products[SelectedCategories[index]])
            options = lodash.map(products[SelectedCategories[index]], function (val) {
                return val;
            })
        else options = []

        // products[SelectedCategories[index]].map((val) => console.log(val))
        return (
            <MDBSelect
                search
                style={{margin: 0}}
                getValue={selectedValue => this.onSelectedProductUpdate(id, selectedValue[0])}
                options={options}
                selected={SelectedProducts[index]}
            />
        );
    };

    onSelectedProductUpdate = (id, selectedValue) => {
        let {
            SelectedCategories,
            SelectedProducts,
            products,
            SelectedProductPrices,
            isMounted,
            discount
        } = this.state;
        const index = id - 1;
        const category = SelectedCategories[index];

        if (isMounted
            && selectedValue !== this.notSelected
            && typeof selectedValue != "undefined"
            && selectedValue !== SelectedProducts[id - 1]
            && typeof category != 'undefined'
        ) {
            SelectedProductPrices[index] = products[category][selectedValue].price
            SelectedProducts[index] = selectedValue;
            const subTotal = this.computeSubtotal()
            const total = subTotal - discount;
            this.setState({SelectedProducts, SelectedProductPrices, subTotal, total});
        }
    };

    computeSubtotal = () => {
        return this.state.SelectedProductPrices.reduce((value, newVal) => {
            return value + newVal;
        });
    }





    render() {
        const {rowCount, SelectedProductPrices, subTotal, total, discount} = this.state;
        return (
            <MDBContainer style={{paddingTop: 50}}>
                <MDBRow className="d-flex justify-content-center">
                    <MDBCol size="12" xl="12">
                        <MDBCard>
                            <MDBCardBody>
                                <MDBCardHeader className="form-header blue-gradient rounded">
                                    <h3 className="my-3">
                                        <MDBIcon icon="dollar-sign"/> Sale
                                    </h3>
                                </MDBCardHeader>
                                <form>
                                    <div className="grey-text">
                                        {/*---------- Row 1 ----------*/}
                                        <MDBRow>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Customer Name"
                                                    group
                                                    type="text"
                                                    validate
                                                    onChange={(event) =>
                                                        this.setState({customerName: event.target.value})
                                                    }
                                                    name="productName"
                                                />
                                            </MDBCol>
                                            <MDBCol sm="1" className="mb-3"/>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Date"
                                                    disabled={true}
                                                    group
                                                    type="text"
                                                    value={getCurrentDate()}
                                                />
                                            </MDBCol>
                                            <MDBCol sm="3">
                                                <MDBBtn color="red" onClick={this.onAdd}>
                                                    Add
                                                </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol>
                                                <MDBTable>
                                                    <MDBTableHead>
                                                        <tr>
                                                            <th>#</th>
                                                            <th>Category</th>
                                                            <th>Product</th>
                                                            <th>Price</th>
                                                        </tr>
                                                    </MDBTableHead>
                                                    <MDBTableBody>
                                                        {lodash.range(1, rowCount + 1).map((id) => {
                                                            return (
                                                                <Row key={id}
                                                                     id={id}
                                                                     categoryComponent={this.categoryComponent}
                                                                     productComponent={this.productComponent}
                                                                     price={SelectedProductPrices[id - 1]}
                                                                />
                                                            )
                                                        })
                                                        }
                                                    </MDBTableBody>
                                                </MDBTable>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Subtotal " value={subTotal} disabled type="number" />
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Discount " value={discount} type="number"  onChange={val => {
                                                    let disc = 0
                                                    if (val.target.value !== '') {
                                                        disc = parseInt(val.target.value);
                                                    }
                                                    const subTotal = this.computeSubtotal();
                                                    const total = subTotal - disc;
                                                    this.setState({discount: disc, subTotal, total})
                                                }}/>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Total " value={total} disabled type="number" />
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBLink   to={{pathname: "/invoice",data: this.state.Items()}}>
                                                    <MDBBtn> Enter Bill </MDBBtn>
                                                </MDBLink>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol>
                                                <MDBDatePicker onInputChange={e => console.log(e)} />
                                            </MDBCol>
                                        </MDBRow>
                                    </div>
                                    <div className="text-center mt-4"/>
                                </form>
                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        );
    }
}

export default SalePage;
