import React, {Component} from "react";
import lodash from "lodash"
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBCardHeader,
    MDBCol,
    MDBContainer,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBSelect,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBLink,
    MDBDatePicker,
    MDBTimePicker
} from "mdbreact";
import axios from "axios";

import getCurrentDate from "../utils/get_current_date"

const Row = ({id, categoryComponent, productComponent, quantity, price}) => {
    return (
        <tr>
            <td>{id}</td>
            <td>{categoryComponent(id)}</td>
            <td>{productComponent(id)}</td>
            <td>{quantity(id)}</td>
            <td>{price}</td>
        </tr>
    );
}


class SalePage extends Component {
    notSelected = "Chose your option";

    onEnter = () =>{
        const {
            SelectedIds,
            SelectedPrices,
            SelectedQty,
            SelectedProducts,
            rowCount,
            subTotal,
            discount,
            total,
            customerName,
            date,
            time,
        } = this.state;
        //    Data Format
        // {
        //  sno: index
        //  dec: Product Name,
        //  qty: 1 (Quantity)
        //  rate: Price
        // }
        let items = lodash.range(rowCount).map(index => {
            const qty = SelectedQty[index];
            return {
                sno: index, // Row No.
                desc: SelectedProducts[index], // Product Name
                qty: qty, // Product Quantity
                rate: SelectedPrices[index] / qty, // Product 1 Qty Price
                Product_id: SelectedIds[index], // Product ID
            }
        })
        const data = {
            subTotal: subTotal,
            discount: discount,
            total: total,
            items: items,
            itemsLength: items.length,
            customerName: customerName,
            date: date,
            time: time,
        };
        console.log(data);
        // axios.post("http://localhost/spare_parts/sales.php", data);
        this.props.history.push({
            pathname: "/invoice",
            data: data
        })

    }

    state = {
        isMounted: false,
        customerName: "",
        date: "",
        time: "",
        rowCount: 1,
        Items: this.onEnter,
        SelectedCategory:[this.notSelected],
        SelectedProducts:[this.notSelected],
        SelectedPrices:[0],
        SelectedQty:[1],
        SelectedIds:[""],
        subTotal: 0,
        discount: 0,
        total: 0,
        categories: [
            // {
            //     text: "Category Name",
            //     value: "Category Value"
            // },
        ],
        products: {
            // Data Format
            // Category_Name: {
            //     Product_Name: {
            //         text: "Product Name",
            //         value: "Product Value"
            //         price: "Product Price"
            //         id: Product_id,
            //         qty: Product_Quantity,
            //     },
            // },
        },
    };

    componentDidMount = async () => {
        let {categories, products} = this.state;
        await axios
            .post("http://localhost/spare_parts/show_category.php")
            .then((response) => {
                    response.data.map((category) => {
                        const newCategory = {text: category.Category_Name, value: category.Category_Name};
                        categories = [...categories, newCategory];
                    });
                }
            );
        await axios
            .post("http://localhost/spare_parts/allproducts.php")
            .then((response) => {
                    response.data.map((product) => {

                        const {Product_Name, Category_Name, Product_Retail_Price,id, Product_Quantity } = product;
                        const newProduct = {}
                        newProduct[Product_Name] = {
                            text: Product_Name,
                            value: Product_Name,
                            price: parseInt(Product_Retail_Price),
                            id:id,
                            qty: Product_Quantity,

                        };
                        if (!products.hasOwnProperty(product.Category_Name))
                            products[Category_Name] = newProduct;
                        else
                            products[Category_Name][Product_Name] = newProduct[Product_Name];
                    });
                }
            );
        this.setState({products, categories});
        this.setState({isMounted: true});
    };
    onAdd = () => {
        const {
            rowCount,
            SelectedCategory,
            SelectedProducts,
            SelectedPrices,
            SelectedQty,
            SelectedIds
        } = this.state;
        const categories = [...SelectedCategory, this.notSelected]
        const products = [...SelectedProducts, this.notSelected]
        const prices = [...SelectedPrices, 0]
        const ids = [...SelectedIds, "None"]
        const qty = [...SelectedQty, 1]
        this.setState({
            rowCount: rowCount + 1,
            SelectedCategory: categories,
            SelectedProducts: products,
            SelectedPrices: prices,
            SelectedQty: qty,
            SelectedIds: ids
        });
    };

    categoryComponent = (id) => {
        const {categories, SelectedCategory} = this.state;
        const index = id - 1;
        return (
            <MDBSelect
                search
                id={`${id+5}`}
                multiple={false}
                style={{margin: 0}}
                options={categories}
                getValue={value => this.onSelectedCategoryUpdate(index, value[0])}
                selected={SelectedCategory[index]}
            />
        );
    };

    onSelectedCategoryUpdate = async (index, selectedValue) => {
        let {
            isMounted,
            discount,
            SelectedCategory,
        } = this.state;

        if ( isMounted
            && typeof selectedValue != "undefined"
            && selectedValue !== SelectedCategory[index]
        ) {
            SelectedCategory[index] = selectedValue;
            const subTotal = this.computeSubtotal();
            const total = subTotal - discount;
            this.setState({SelectedCategory, subTotal, total});
        }
    };

    productComponent = (id) => {
        const {
            products,
            SelectedProducts,
            SelectedCategory
        } = this.state;

        const index = id - 1;
        let options;
        if (products[SelectedCategory[index]]) {
            options = lodash.map(products[SelectedCategory[index]], function (val) {
                return val;
            })
        }
        else options = [];
        console.log(SelectedProducts[index], index)
        return <MDBSelect
            id={`${id+5}`}
            search
            style={{margin: 0}}
            getValue={product => this.onSelectedProductUpdate(index, product[0])}
            options={options}
            selected={SelectedProducts[index]}
        />
    };
    onSelectedProductUpdate = async (index, product) => {
        const {
            products,
            isMounted,
            discount,
            SelectedProducts,
            SelectedCategory,
            SelectedPrices,
            SelectedIds
        } = this.state;
        const category = SelectedCategory[index];
        console.log(product !== SelectedProducts[index], product, SelectedProducts[index], index)
        if (isMounted
            && product !== this.notSelected
            && typeof product != "undefined"
            && product !== SelectedProducts[index]
            && typeof category != 'undefined'
        ) {
            // console.log(index, SelectedProducts, selectedValue)
            const items = SelectedProducts.map((v, i) => i === index ? product : v)
            // console.log(items)
            await this.setState({SelectedProducts: items});
        }

        // let {
        //     products,
        //     isMounted,
        //     discount,
        //     SelectedProducts,
        //     SelectedCategory,
        //     SelectedPrices,
        //     SelectedIds
        // } = this.state;
        // const category = SelectedCategory[index];
        //
        // if (isMounted
        //     && selectedValue !== this.notSelected
        //     && typeof selectedValue != "undefined"
        //     && selectedValue !== SelectedProducts[index]
        //     && typeof category != 'undefined'
        // ) {
        //     SelectedPrices[index] = products[category][selectedValue].price;
        //     SelectedIds[index] = products[category][selectedValue].id;
        //     SelectedProducts[index] = selectedValue;
        //     const subTotal = this.computeSubtotal()
        //     const total = subTotal - discount;
        //     this.setState({SelectedProducts, SelectedIds, SelectedPrices, subTotal, total});
        // }
    };

    quantityComponent = (id) =>{
        let {
            SelectedQty,
            SelectedProducts,
            SelectedCategory,
            SelectedPrices,
            products,
            subTotal,
            total,
            discount,
        } = this.state;
        const index = id - 1 ;

        return(
            <MDBInput
                style={{marginTop:"-1.5rem"}}
                label="Quantity"
                group
                type="number"
                value={SelectedQty[index]}
                validate
                onChange={(event) => {

                    const product = SelectedProducts[index];
                    const category = SelectedCategory[index];

                    if(event.target.value !== '' &&
                        typeof products[category] != "undefined" &&
                        typeof products[category][product] != 'undefined'
                    ){
                        const quantity = parseInt(event.target.value);
                        SelectedPrices[index] = products[category][product].price * quantity
                        SelectedQty[index] = quantity;
                        subTotal = this.computeSubtotal();
                        total = subTotal - discount;
                        this.setState({SelectedPrices, SelectedQty, subTotal, total});
                    }

                }}
                name="productName"
            />
        )
    };



    computeSubtotal = () => {
        return this.state.SelectedPrices.reduce((val, newVal) => val + newVal)
    }

    render() {
        const {rowCount, SelectedPrices, subTotal, total, discount} = this.state;
        return (
            <MDBContainer style={{paddingTop: 50}}>
                <MDBRow className="d-flex justify-content-center">
                    <MDBCol size="12" xl="12">
                        <MDBCard>
                            <MDBCardBody>
                                <MDBCardHeader className="form-header blue-gradient rounded">
                                    <h3 className="my-3">
                                        <MDBIcon icon="dollar-sign"/> Sale
                                    </h3>
                                </MDBCardHeader>
                                <form>
                                    <div className="grey-text">
                                        {/*---------- Row 1 ----------*/}
                                        <MDBRow>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Customer Name"
                                                    group
                                                    type="text"
                                                    validate
                                                    onChange={(event) =>
                                                        this.setState({customerName: event.target.value})
                                                    }
                                                    name="productName"
                                                />
                                            </MDBCol>
                                            <MDBCol sm="1" className="mb-3"/>
                                            <MDBCol sm="2" className="mt-2">
                                                <MDBDatePicker  getValue={value => this.setState(
                                                    {date: `${value.getDate()}-${value.getMonth()}-${value.getYear()+1900}`}
                                                )} />
                                            </MDBCol>
                                            <MDBCol sm="2" className="mb-3">
                                                <MDBTimePicker  getValue={value => this.setState({time: value})}  id={"Time"}/>
                                            </MDBCol>
                                            <MDBCol sm="3">
                                                <MDBBtn color="red" onClick={this.onAdd}>
                                                    Add
                                                </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol>
                                                <MDBTable>
                                                    <MDBTableHead>
                                                        <tr>
                                                            <th>#</th>
                                                            <th>Category</th>
                                                            <th>Product</th>
                                                            <th>Qty</th>
                                                            <th>Price</th>
                                                        </tr>
                                                    </MDBTableHead>
                                                    <MDBTableBody>
                                                        {lodash.range(1, rowCount + 1).map((id) =>
                                                            <Row key={id}
                                                                 id={id}
                                                                 categoryComponent={this.categoryComponent}
                                                                 productComponent={this.productComponent}
                                                                 price={SelectedPrices[id - 1]}
                                                                 quantity={this.quantityComponent}
                                                            />
                                                        )
                                                        }
                                                    </MDBTableBody>
                                                </MDBTable>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Subtotal " value={subTotal} disabled type="number" />
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Discount " value={discount} type="number"  onChange={val => {
                                                    let disc = 0
                                                    if (val.target.value !== '') {
                                                        disc = parseInt(val.target.value);
                                                    }
                                                    const subTotal = this.computeSubtotal();
                                                    const total = subTotal - disc;
                                                    this.setState({discount: disc, subTotal, total})
                                                }}/>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Total " value={total} disabled type="number" />
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBBtn onClick={()=> {
                                                    this.state.SelectedProducts.map((value, index) => console.log(value, index))
                                                }}> Enter Bill </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                    </div>
                                    <div className="text-center mt-4"/>
                                </form>
                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        );
    }
}

export default SalePage;
