import React, {Component} from "react";
import lodash from "lodash"
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBCardHeader,
    MDBCol,
    MDBContainer,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBSelect,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBLink,
    MDBDatePicker,
    MDBTimePicker
} from "mdbreact";
import axios from "axios";

import getCurrentDate from "../utils/get_current_date"

const Row = ({id, categoryComponent, productComponent, quantity, price}) => {
    return (
        <tr>
            <td>{id}</td>
            <td>{categoryComponent(id)}</td>
            <td>{productComponent(id)}</td>
            <td>{quantity(id)}</td>
            <td>{price}</td>
        </tr>
    );
}


class SalePage extends Component {
    notSelected = "Chose your option";

    onEnter = () =>{
        const {
            SelectedItems,
            rowCount,
            subTotal,
            discount,
            total,
            customerName,
            date,
            time,
        } = this.state;
        //    Data Format
        // {
        //  sno: index
        //  dec: Product Name,
        //  qty: 1 (Quantity)
        //  rate: Price
        // }
        let items = lodash.range(rowCount).map(index => {
            return {
                sno: index, // Row No.
                desc: SelectedItems[index].product, // Product Name
                qty: SelectedItems[index].qty, // Product Quantity
                rate: SelectedItems[index].price / SelectedItems[index].qty, // Product 1 Qty Price,  ""SelectedItems[index].price"" Contains the total price of the item
                Product_id: SelectedItems[index].productId, // Product ID
            }
        })
        const data = {
            subTotal: subTotal,
            discount: discount,
            total: total,
            items: items,
            itemsLength: items.length,
            customerName: customerName,
            date: date,
            time: time,
        };
        console.log(data);
        // axios.post("http://localhost/spare_parts/sales.php", data);
        this.props.history.push({
            pathname: "/invoice",
            data: data
        })

    }

    newItem = {
        category: this.notSelected,
        product:this.notSelected,
        price:0,
        qty:1,
        productId:""
    }

    state = {
        isMounted: false,
        customerName: "",
        date: "",
        time: "",
        rowCount: 1,
        Items: this.onEnter,
        SelectedItems: [
            {
                category: "Chose your option",
                product: "Chose your option",
                price:0,
                qty:1,
                productId:""
            },
        ],
        subTotal: 0,
        discount: 0,
        total: 0,
        categories: [
            // {
            //     text: "Category Name",
            //     value: "Category Value"
            // },
        ],
        products: {
            // Data Format
            // Category_Name: {
            //     Product_Name: {
            //         text: "Product Name",
            //         value: "Product Value"
            //         price: "Product Price"
            //         id: Product_id,
            //         qty: Product_Quantity,
            //     },
            // },
        },
    };

    componentDidMount = async () => {
        let {categories, products} = this.state;
        await axios
            .post("http://localhost/spare_parts/show_category.php")
            .then((response) => {
                    response.data.map((category) => {
                        const newCategory = {text: category.Category_Name, value: category.Category_Name};
                        categories = [...categories, newCategory];
                    });
                }
            );
        await axios
            .post("http://localhost/spare_parts/allproducts.php")
            .then((response) => {
                    response.data.map((product) => {

                        const {Product_Name, Category_Name, Product_Retail_Price,id, Product_Quantity } = product;
                        const newProduct = {}
                        newProduct[Product_Name] = {
                            text: Product_Name,
                            value: Product_Name,
                            price: parseInt(Product_Retail_Price),
                            id:id,
                            qty: Product_Quantity,

                        };
                        if (!products.hasOwnProperty(product.Category_Name))
                            products[Category_Name] = newProduct;
                        else
                            products[Category_Name][Product_Name] = newProduct[Product_Name];
                    });
                }
            );
        this.setState({products, categories})
        this.setState({isMounted: true})
    };

    onAdd = () => {
        let {
            rowCount,
            SelectedItems
        } = this.state;
        SelectedItems[rowCount] = {
            category: "Chose your option",
            product: "Chose your option",
            price:0,
            qty:1,
            productId:""
        };
        rowCount += 1;
        this.setState({SelectedItems, rowCount});
    };

    categoryComponent = (id) => {
        const {categories, SelectedItems} = this.state;
        const index = id - 1;
        return (
            <MDBSelect
                search
                style={{margin: 0}}
                options={categories}
                getValue={value => this.onSelectedCategoryUpdate(index, value[0])}
                selected={SelectedItems[index].category}
            />
        );
    };

    onSelectedCategoryUpdate = (index, selectedValue) => {
        let {
            isMounted,
            discount,
            SelectedItems
        } = this.state;

        if (isMounted
            && typeof selectedValue != "undefined"
            && selectedValue !== SelectedItems[index].category
        ) {
            SelectedItems[index].category = selectedValue;
            const subTotal = this.computeSubtotal();
            const total = subTotal - discount;
            this.setState({SelectedItems, subTotal, total});
        }
    };

    productComponent = (id) => {
        const {products, SelectedItems} = this.state;
        const index = id - 1;
        let options;
        if (products[SelectedItems[index].category]) {
            options = lodash.map(products[SelectedItems[index].category], function (val) {
                return val;
            })
        }
        else options = []
        console.log(options, SelectedItems)
        return (
            <MDBSelect
                search
                style={{margin: 0}}
                getValue={value => this.onSelectedCategoryUpdate(index, value[0])}
                options={options}
                selected={SelectedItems[index].product}
            />
        );
    };

    onSelectedProductUpdate = (index, selectedValue) => {
        let {
            products,
            isMounted,
            discount,
            SelectedItems
        } = this.state;
        const category = SelectedItems[index].category;

        if (isMounted
            && selectedValue !== this.notSelected
            && typeof selectedValue != "undefined"
            && selectedValue !== SelectedItems[index].product
            && typeof category != 'undefined'
        ) {
            const {price, id} = products[category][selectedValue];
            SelectedItems[index].price = price;
            SelectedItems[index].productId = id;
            SelectedItems[index].product = selectedValue;
            console.log(SelectedItems);
            const subTotal = this.computeSubtotal()
            const total = subTotal - discount;
            this.setState({SelectedItems, subTotal, total});
        }
    };

    quantityComponent = (id) =>{
        let {
            SelectedItems,
            products,
            subTotal,
            total,
            discount,
        } = this.state;
        const index = id - 1 ;

        return(
            <MDBInput
                style={{marginTop:"-1.5rem"}}
                label="Quantity"
                group
                type="number"
                value={SelectedItems[index].qty}
                validate
                onChange={(event) => {

                    const {product, category} = SelectedItems[index];

                    if(event.target.value !== '' &&
                        typeof products[category] != "undefined" &&
                        typeof products[category][product] != 'undefined'
                    ){
                        const quantity = parseInt(event.target.value);
                        SelectedItems[index].price = products[category][product].price * quantity
                        SelectedItems[index].qty = quantity;
                        subTotal = this.computeSubtotal();
                        total = subTotal - discount;
                        this.setState({SelectedItems, subTotal, total});
                    }

                }}
                name="productName"
            />
        )
    };



    computeSubtotal = () => {
        const {SelectedItems} = this.state;
        let item, subtotal = 0;
        for (item of SelectedItems) subtotal += item.price;
        return subtotal;
    }





    render() {
        const {rowCount, SelectedItems, subTotal, total, discount} = this.state;
        return (
            <MDBContainer style={{paddingTop: 50}}>
                <MDBRow className="d-flex justify-content-center">
                    <MDBCol size="12" xl="12">
                        <MDBCard>
                            <MDBCardBody>
                                <MDBCardHeader className="form-header blue-gradient rounded">
                                    <h3 className="my-3">
                                        <MDBIcon icon="dollar-sign"/> Sale
                                    </h3>
                                </MDBCardHeader>
                                <form>
                                    <div className="grey-text">
                                        {/*---------- Row 1 ----------*/}
                                        <MDBRow>
                                            <MDBCol sm="4" className="mb-3">
                                                <MDBInput
                                                    label="Customer Name"
                                                    group
                                                    type="text"
                                                    validate
                                                    onChange={(event) =>
                                                        this.setState({customerName: event.target.value})
                                                    }
                                                    name="productName"
                                                />
                                            </MDBCol>
                                            <MDBCol sm="1" className="mb-3"/>
                                            <MDBCol sm="2" className="mt-2">
                                                <MDBDatePicker  getValue={value => this.setState(
                                                    {date: `${value.getDate()}-${value.getMonth()}-${value.getYear()+1900}`}
                                                )} />
                                            </MDBCol>
                                            <MDBCol sm="2" className="mb-3">
                                                <MDBTimePicker  getValue={value => this.setState({time: value})}  id={"Time"}/>
                                            </MDBCol>
                                            <MDBCol sm="3">
                                                <MDBBtn color="red" onClick={this.onAdd}>
                                                    Add
                                                </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol>
                                                <MDBTable>
                                                    <MDBTableHead>
                                                        <tr>
                                                            <th>#</th>
                                                            <th>Category</th>
                                                            <th>Product</th>
                                                            <th>Qty</th>
                                                            <th>Price</th>
                                                        </tr>
                                                    </MDBTableHead>
                                                    <MDBTableBody>
                                                        {lodash.range(1, rowCount + 1).map((id) =>
                                                            <Row key={id}
                                                                 id={id}
                                                                 categoryComponent={this.categoryComponent}
                                                                 productComponent={this.productComponent}
                                                                 price={SelectedItems[id - 1].price}
                                                                 quantity={this.quantityComponent}
                                                            />
                                                        )
                                                        }
                                                    </MDBTableBody>
                                                </MDBTable>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Subtotal " value={subTotal} disabled type="number" />
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Discount " value={discount} type="number"  onChange={val => {
                                                    let disc = 0
                                                    if (val.target.value !== '') {
                                                        disc = parseInt(val.target.value);
                                                    }
                                                    const subTotal = this.computeSubtotal();
                                                    const total = subTotal - disc;
                                                    this.setState({discount: disc, subTotal, total})
                                                }}/>
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBInput label="Total " value={total} disabled type="number" />
                                            </MDBCol>
                                        </MDBRow>
                                        <MDBRow>
                                            <MDBCol sm="3">
                                                <MDBBtn onClick={this.onEnter}> Enter Bill </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                    </div>
                                    <div className="text-center mt-4"/>
                                </form>
                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        );
    }
}

export default SalePage;
