import React, {Component} from "react";
import {MDBDataTable} from 'mdbreact';
import "./all_product.css"
import axios from "axios";

class AllProducts extends Component {



    quantityComponent = (index, value = null) =>{
        const {rows} = this.state.data;
        const quantity = rows.length > 0 ? rows[index].Product_Quantity : parseInt(value);
        return (
            <div className="md-form" style={{margin: 0}}>
                <input
                    type="number"
                    id={index + 1}
                    onChange={event => this.onQuantityChange(index, event.target.value)}
                    min={0}
                    value={quantity}
                    className="form-control"
                />
            </div>
        );
    }

    onQuantityChange = (index, value) => {
        let quantity = 0;
        if (value !== "") quantity = parseInt(value);
        let { data } = this.state;
        data.rows[index].Product_Quantity = quantity;
        this.setState({data})
    }

    state = {
        data: {
            columns: [
                {
                    label: 'Name',
                    field: 'Product_Name',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'C.Name',
                    field: 'Category_Name',
                    sort: 'asc',
                    width: 270
                },
                {
                    label: 'P.Brand',
                    field: 'Product_Brand',
                    sort: 'asc',
                    width: 200
                },
                {
                    label: 'P.Code',
                    field: 'Product_Code',
                    sort: 'asc',
                    width: 100
                },
                {
                    label: 'Qty',
                    field: 'qty',
                    sort: 'asc',
                    width: 100
                },
                {
                    label: 'R.Price',
                    field: 'Product_Retail_Price',
                    sort: 'asc',
                    width: 350
                },
            ],
            rows: []
        }
    };

    componentDidMount = async () => {
        let {data} = this.state;
        let rows = data.rows;
        await axios
            .post("http://localhost/spare_parts/allproducts.php")
            .then((response) => {
                    response.data.map((product, index) => {
                        product["qty"] = this.quantityComponent(index, product.Product_Quantity);
                        rows = [...rows, product];
                    });
                }
            );
        data.rows = rows;
        this.setState({data});
    };

    onSave = () =>{
        const products = this.state.data.rows.map(value => {
            delete value.qty;
            return value;
        })

        const data = {
            items: products,
        }

        console.log(data);
        axios.post("http://localhost/spare_parts/update_product.php", data)
            .then(value => console.log(value));
    }

    render() {
        let { columns, rows } = this.state.data;
        if (rows.length > 0)
            rows.map((value, index) => rows[index].qty = this.quantityComponent(index))
        return (
                <div>
                    <MDBDataTable
                        // className="form-header blue-gradient rounded"
                        theadColor={"blue-gradient"}
                        striped
                        barReverse={true}
                        theadTextWhite={true}
                        hover
                        noBottomColumns={true}
                        data={{columns,rows}}
                    />
                    <button onClick={this.onSave}>Save</button>
                </div>
        );
    }
}

export default AllProducts;





