import React,{Component} from "react";
import {MDBBtn, MDBCol, MDBContainer, MDBDataTable, MDBRow} from "mdbreact";

class AllExpanses extends Component{
    state = {
        data: {
            columns: [
                {
                    label: 'Name',
                    field: 'Product_Name',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'C.Name',
                    field: 'Category_Name',
                    sort: 'asc',
                    width: 270
                },
                {
                    label: 'P.Brand',
                    field: 'Product_Brand',
                    sort: 'asc',
                    width: 200
                },
                {
                    label: 'P.Code',
                    field: 'Product_Code',
                    sort: 'asc',
                    width: 100
                },
                {
                    label: 'Qty',
                    field: 'qty',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'R.Price',
                    field: 'Product_Retail_Price',
                    sort: 'asc',
                    width: 350
                },
                {
                    label: 'Profit',
                    field: 'Profit',
                    sort: 'asc',
                    width: 350
                },
            ],
            rows: []
        },
        grossProfit: 0
    };

    render() {
        let { columns, rows } = this.state.data;
        if (rows.length > 0)
            rows.map((value, index) => {
                rows[index].qty = this.quantityComponent(index);
                // rows[index].Product_PurchasePrice;
                // rows[index].Product_Retail_Price;
            })
        return (
            <div>
                <MDBDataTable
                    // className="form-header blue-gradient rounded"
                    theadColor={"blue-gradient"}
                    striped
                    barReverse={true}
                    theadTextWhite={true}
                    hover
                    noBottomColumns={true}
                    data={{columns,rows}}
                />
            </div>
        );
    }
}

export default AllExpanses;
