import React, { Component } from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBIcon,
  MDBCardHeader,
  MDBBtn,
  MDBInput,
  MDBSelect,
  MDBTable,
  MDBTableBody,
  MDBTableHead,
} from "mdbreact";

class SalePage extends Component {
  state = {
    CustomerName: "",
    CategoryName: "",
  };

  getCurrentDate = (separator = "      ") => {
    let newDate = new Date();
    let date = newDate.getDate();
    let month = newDate.getMonth() + 1;
    let year = newDate.getFullYear();
    let hours = newDate.getHours();
    let mintues = newDate.getMinutes();
    let seconds = newDate.getSeconds();
    return `${year}/${
      month < 10 ? `0${month}` : `${month}`
    }/${date}${separator}${hours}:${mintues}:${seconds}`;
  };

  columns = [
    {
      label: "#",
      field: "id",
      sort: "asc",
    },
    {
      label: "Category",
      field: "category",
      sort: "asc",
      minimal: "lg",
    },
    {
      label: "Product",
      field: "product",
      sort: "asc",
      minimal: "lg",
    },
    {
      label: "Price",
      field: "price",
      sort: "asc",
      minimal: "lg",
    },
  ];

  rows_outline_btn = [
    {
      id: <h5 style={{ marginTop: 13, fontSize: 15, fontWeight: "300" }}>2</h5>,
      category: (
        <MDBSelect
          search
          style={{ margin: 0 }}
          options={this.state.categories}
          selected="Choose your option"
          getValue={(val) =>
            this.setState({
              categoryName: val[0],
            })
          }
        />
      ),
      last: (
        <MDBSelect
          search
          style={{ margin: 0 }}
          options={this.state.categories}
          selected="Choose your option"
          getValue={(val) =>
            this.setState({
              categoryName: val[0],
            })
          }
        />
      ),
      price: "20",
    },
  ];

  render() {
    return (
      <MDBContainer style={{ paddingTop: 50 }}>
        <MDBRow className="d-flex justify-content-center">
          <MDBCol size="12" xl="12">
            <MDBCard>
              <MDBCardBody>
                <MDBCardHeader className="form-header blue-gradient rounded">
                  <h3 className="my-3">
                    <MDBIcon icon="dollar-sign" /> Sale
                  </h3>
                </MDBCardHeader>
                <form>
                  <div className="grey-text">
                    {/*---------- Row 1 ----------*/}
                    <MDBRow>
                      <MDBCol sm="4" className="mb-3">
                        <MDBInput
                          label="Customer Name"
                          group
                          type="text"
                          validate
                          onChange={(event) =>
                            this.setState({ CustomerName: event.target.value })
                          }
                          name="productName"
                        />
                      </MDBCol>
                      <MDBCol sm="1" className="mb-3"></MDBCol>
                      <MDBCol sm="4" className="mb-3">
                        <MDBInput
                          label="Date"
                          disabled={true}
                          group
                          type="text"
                          value={this.getCurrentDate()}
                        />
                      </MDBCol>
                    </MDBRow>
                    <MDBRow>
                      <MDBTable>
                        <MDBTableHead columns={this.columns} />
                        <MDBTableBody rows={this.rows_outline_btn} />
                      </MDBTable>
                    </MDBRow>
                  </div>
                  <div className="text-center mt-4"></div>
                </form>
                <MDBBtn
                  color="light-blue"
                  className="mb-3"
                  onClick={this.onAdd}
                >
                  Add
                </MDBBtn>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
}

export default SalePage;
